import React, {Component} from 'react';
import { Form, Button } from 'react-bootstrap';
import intellitel from '';
import axios from 'axios';
import './App.css';
import './intlTelInput.min.css';

class App extends Component {
    componentDidMount () {
        const script = document.createElement("script");

        script.src = "https://dev.webircle.com/static_files/public/intlTelInput.js";

        document.body.appendChild(script);
        let input = document.querySelector("#phone");
        window.intlTelInput(input, {
            separateDialCode: true,
            initialCountry: "IN",
        });
    }

    render(){
        return (
            <Form className="form-signin">
                <Form.Group>
                    <Form.Control type="tel" id="phone" placeholder="Enter Phone No" />
                </Form.Group>
                <Button style={{"backgroundColor":"#47e5f7", "borderColor":"#47e5f7" }} type="submit" size="lg" block>
                    Login with OTP
                </Button>
                or
                <Form.Group controlId="formBasicEmail">
                    <Form.Control type="email" placeholder="Enter email" />
                </Form.Group>
                <Form.Group controlId="formBasicPassword" style={{"margin":"0"}}>
                    <Form.Control type="password" placeholder="Password" />
                </Form.Group>
                <div style={{"float":"right"}}>
                    Forgot password?
                </div>
                <Button style={{"backgroundColor":"#541672", "borderColor":"#541672" }} type="submit" size="lg" block>
                    Login
                </Button>
                or
                <Button style={{"backgroundColor":"#3b5998", "borderColor":"#3b5998" }} type="submit" size="lg" block>
                    Facebook
                </Button>
                <Button variant="light" type="submit" size="lg" block>
                    Google
                </Button>
            </Form>
        );
    }

}

export default App;
